import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ZService } from 'src/app/services/z.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  kor_ime: string = "";
  lozinka: string = "";
  tip: string = "Ucenik";
  poruka: string = "";

  constructor(private service: ZService, private router: Router) {}

  login() {
    if (this.kor_ime === "") this.poruka = "Morate uneti korisnicko ime.";
    else if (this.lozinka === "") this.poruka = "Morate uneti lozinku.";
    else {
      this.service.login(this.kor_ime, this.lozinka, this.tip).subscribe(
        data => {
          if (!data) this.poruka = "Losi podaci. Pokusajte ponovo!";
          else {
            this.poruka = "";
            localStorage.setItem("ulogovani", this.kor_ime);
            if (this.tip === "Ucenik") this.router.navigate(["ucenik"]);
            else this.router.navigate(["nastavnik"]);
          }
        }
      );
    }
  }

}
<h1>Login stranica</h1>
<hr>
<table>
  <tr>
    <td>Unesite korisnicko ime:</td>
    <td>
      <input type="text" [(ngModel)]="kor_ime">
    </td>
  </tr>
  <tr>
    <td>Unesite lozinku:</td>
    <td>
      <input type="password" [(ngModel)]="lozinka">
    </td>
  </tr>
  <tr>
    <td>Izaberite tip:</td>
    <td>
      <label for="ucenik">Ucenik</label>
      <input type="radio" name="login" id="ucenik" value="Ucenik" [(ngModel)]="tip">
      <label for="nastavnik">Nastavnik</label>
      <input type="radio" name="login" id="nastavnik" value="Nastavnik" [(ngModel)]="tip">
    </td>
  </tr>
  <tr>
    <td colspan="2">
      <input type="button" value="Prijava" (click)="login()">
    </td>
  </tr>
</table>
<hr>
<p *ngIf="poruka !== ''" class="red">
  {{poruka}}
</p>
